.navbarBackground {
  background-color: #181818;
  width: 60px;
  height: 100vh;
  position: fixed;
  display: grid;
  grid-template-rows: 1fr 2fr 1fr;
}

.listLanguage {
  grid-row-start: 1;
  margin-top: 25px;

  li {
    list-style: none;
    text-align: center;
    margin-bottom: 5px;
  }

  a {
    text-decoration: none;
    color: #999999;
    font-size: 1.2em;
    font-weight: 900;
  }

  a:hover {
    color: #35f425;
  }

  .english {
    text-decoration: underline;
    text-decoration-color: #35f425;
  }
}

.navlist {
  grid-row-start: 2;
  align-self: center;

  .navlinks {
    height: 60px;
    list-style: none;
    text-align: center;
    display: grid;

    a {
      text-decoration: none;
      color: #35f425;
      font-size: 0.7em;
      font-weight: 500;
      align-self: center;
    }

    img {
      height: 35px;
    }

    .about,
    .contact {
      height: 30px;
    }
  }
}

.activePage {
  background-color: #212121 !important;
  border-top-style: solid;
  border-top-color: #35f425;
  border-bottom-style: solid;
  border-bottom-color: #35f425;
  border-width: 1px;
  transition: 1s;
}

.social {
  grid-row-start: 3;
  align-self: end;
  margin-bottom: 25px;

  li {
    list-style: none;
    text-align: center;
    margin-top: 5px;
  }

  a {
    text-decoration: none;
    color: #999999;
    font-size: 1.5em;
    font-weight: 500;
  }

  a:hover {
    color: #35f425;
  }
}

