@import url("https://fonts.googleapis.com/css?family=Exo:400,800&display=swap");

$greenColor: #35f425;

* {
	box-sizing: border-box;
	padding: 0px;
	margin: 0px;
	font-family: "Exo", sans-serif;
}

html,
body {
	width: 100%;
	height: 100%;
	display: block;
	//overflow: hidden;
	background: #212121;
}

main {
	height: 100%;
}

.transition-fade {
	transition: 0.4s;
	opacity: 1;
}

html.is-animating .transition-fade {
	opacity: 0;
}

.transition-popin {
	transition-delay: 2s;
	opacity: 1;
}

html.is-animating .transition-popin {
	opacity: 0;
}

.homePage {
	margin-left: 60px;
	background-color: #212121;
	height: 100vh;
	display: grid;
	grid-template-columns: 0px 1.5fr 2fr;
	grid-template-rows: 1fr 2fr;
	transition: 0.5s;
	overflow: hidden;
}

.videoPlayback {
	filter: blur(4px);
	background-image: url("/Video/reactCode.gif");
	background-repeat: no-repeat;
	background-size: cover;
	min-width: 100%;
	min-height: 100%;
	grid-column-start: 3;
	grid-column-end: 4;
	grid-row-start: 1;
	grid-row-end: 3;
	transition: 0.5s;
	transform: scale(1.1);
	margin-left: 5vh;
}

