sources.style\\_sublimeText.scss:
$color1: #e6e7e8;
$color2: #ff8f8f;
$color3: #404041;
$color4: #ff8f8f;
$color5: #5ec3af;
$color6: #9e76b4;
$color7: #43c7f4;
$color8: #ffcc4e;

body {
	background-color: $color4;
}
*,
*:before,
*:after {
	box-sizing: border-box;
}
// Macbook
.sublime-text {
	margin: 60px auto 50px auto;
	width: 80%;
	overflow: visible;
	transform: scale(0);
	animation: appear 500ms ease-out forwards;

	.screen {
		position: relative;
		height: 450px;
		background-color: $color3;
		border-radius: 8px;
		color: white;
		font-size: 30px;

		.top-bar {
			width: 100%;
			height: 30px;
			position: absolute;
			margin-top: 0;
			background-color: $color1;
			border-radius: 7px 7px 0 0;

			.buttons {
				position: absolute;
				width: 8px;
				height: 8px;
				top: 12px;
				background: $color4;
				border-radius: 50%;
				left: 22px;
				&:after,
				&:before {
					position: absolute;
					content: "";
					width: 8px;
					height: 8px;
					border-radius: 50%;
				}
				&:after {
					left: 12px;
					background-color: $color5;
				}
				&:before {
					left: -12px;
					background-color: darken($color4, 20%);
				}
			}
		}

		.left-bar {
			height: 100%;
			width: 30px;
			position: absolute;
			padding-left: 10px;
			background-color: darken($color3, 7%);
			border-radius: 10px 0 0 7px;

			&:after {
				position: absolute;
				top: 24px;
				content: ". . . . . . . . . . . . . . . . . . . .";
				color: lighten($color3, 10%);
				font-size: 42px;
				line-height: 20px;
			}
		}

		.code {
			position: absolute;
			margin: 32px 0 0 30px;
			width: 90%;
			height: 90%;

			.row {
				position: relative;
				padding: 8px;
				height: 20px;

				p {
					opacity: 0;
					position: relative;
					display: inline-block;
					width: 150px;
					height: 8px;
					background-color: white;
					border-radius: 4px;

					&.green {
						width: random(5) + 25%;
						background-color: $color5;
					}
					&.orange {
						width: random(5) + 15%;
						background-color: $color4;
					}
					&.blue {
						width: random(5) + 17%;
						background-color: $color7;
					}
					&.purple {
						width: random(5) + 25%;
						background-color: $color6;
					}
					&.yellow {
						width: random(5) + 20%;
						background-color: $color8;
					}
					&.transparent {
						background-color: transparent;
						width: 30px;
					}
					&.cursor {
						border-radius: 0;
						margin-left: 1px;
						background-color: $color1;
						width: 2px;
						animation: blink 1s ease 8s infinite;
					}
				}
			}
		}
	}
} /* sublime text */

/* Input text animation */
$cont: 0;
@for $z from 1 through 18 {
	@for $i from 1 through 9 {
		.row#{$z} > p:nth-child(#{$i}) {
			$cont: 400 * $z + $i * 100 + 600;
			$cont4: 400 * $z + $i * 100 + 5000;
			$cont6: 400 * $z + $i * 100 + 1800;
			$cont8: 400 * $z + $i * 100 + 2400;
			$cont10: 400 * $z + $i * 100 + 3000;
			$cont12: 400 * $z + $i * 100 + 3600;
			$cont14: 400 * $z + $i * 100 + 4200;
			$cont16: 400 * $z + $i * 100 + 4800;
			@if $z <= 2 {
				animation: show-text 100ms ease #{$cont}ms forwards;
			} @else if $z <= 4 {
				animation: show-text 100ms ease #{$cont4}ms forwards;
			} @else if $z <= 6 {
				animation: show-text 100ms ease #{$cont6}ms forwards;
			} @else if $z <= 8 {
				animation: show-text 100ms ease #{$cont8}ms forwards;
			} @else if $z <= 10 {
				animation: show-text 100ms ease #{$cont10}ms forwards;
			} @else if $z <= 12 {
				animation: show-text 100ms ease #{$cont12}ms forwards;
			} @else if $z <= 14 {
				animation: show-text 100ms ease #{$cont14}ms forwards;
			} @else {
				animation: show-text 100ms ease #{$cont16}ms forwards;
			}
		}
	}
}

@keyframes blink {
	0% {
		opacity: 0;
	}
	100% {
		opacity: 1;
	}
}

@keyframes show-text {
	0% {
		opacity: 0;
	}
	100% {
		opacity: 1;
	}
}

@keyframes appear {
	0% {
		transform: scale(0);
	}
	50% {
		transform: scale(1.05);
	}
	75% {
		transform: scale(0.95);
	}
	100% {
		transform: scale(1);
	}
}

